    public static final String PASS_TYPE_DOCUMENT_MASTER_DETAILS_BY_PAGING_COUNT = "select count(*) from pass_type_document_master ptdm, pass_type_master ptm , document_master dm where ptdm.pass_type_id =ptm.pass_type_id and ptdm.doc_id =dm.doc_id and ptm.status_cd='A' and dm.status_cd='A' and ptdm.pass_type_doc_id=coalesce(:passTypeDocId,ptdm.pass_type_doc_id) and ptm.pass_type_id=coalesce(:passTypeId,ptm.pass_type_id) and ptdm.status_cd = coalesce(:statusCd, ptdm.status_cd)";

