    public static final String STUDENT_PASS_MASTER_DETAILS_BY_PAGING = "select sps.stud_pass_id, sps.stud_pass_form_no, sps.cust_id, cs.cust_first_name, cs.cust_middle_name, cs.cust_last_name, sps.pass_type_id, ptm.pass_type_name, ptm.pass_type_description, ptm.pass_type_end_date, ptm.pass_type_collection_location, ptm.pass_type_amount, ptm.pass_type_age_limit, sps.stud_pass_created_date, sps.stud_pass_expire_date, sps.routes_id, rm.routes_name, rm.routes_start_location, rm.routes_end_location, sps.from_bus_stop_id, frombsm.bus_stop_name, frombsm.bus_stop_no, sps.to_bus_stop_id, tobsm.bus_stop_name as tobustopname, tobsm.bus_stop_no as tobusstopno, sps.stud_pass_amount, sps.stud_pass_amount_paid_status, sps.school_name, sps.school_address, sps.school_automomus, sps.school_udise_number, sps.school_everyday_start_timing, sps.school_everyday_end_timing, sps.stud_class_name, sps.stud_course_name, sps.stud_roll_no, sps.stud_pass_status,sps.status_cd,sps.remark from student_pass_master sps, customer_master cs, pass_type_master ptm, routes_master rm, bus_stop_master frombsm, bus_stop_master tobsm where sps.cust_id = cs.cust_id and ptm.pass_type_id =sps.pass_type_id and rm.routes_id =sps.routes_id and frombsm.bus_stop_id = sps.from_bus_stop_id and tobsm.bus_stop_id=sps.to_bus_stop_id and sps.stud_pass_id = coalesce(:studPassId, sps.stud_pass_id) and sps.stud_pass_status = coalesce(:studPassStatus, sps.stud_pass_status) and sps.status_cd = coalesce(:statusCd, sps.status_cd) order by :sortName asc limit :pageSize offset :pageOffset";
    public static final String STUDENT_PASS_MASTER_DETAILS_BY_PAGING_COUNT = "select count(*) from student_pass_master sps, customer_master cs, pass_type_master ptm, routes_master rm, bus_stop_master frombsm, bus_stop_master tobsm where sps.cust_id = cs.cust_id and ptm.pass_type_id =sps.pass_type_id and rm.routes_id =sps.routes_id and frombsm.bus_stop_id = sps.from_bus_stop_id and tobsm.bus_stop_id=sps.to_bus_stop_id and sps.stud_pass_id = coalesce(:studPassId, sps.stud_pass_id) and sps.stud_pass_status = coalesce(:studPassStatus, sps.stud_pass_status) and sps.status_cd = coalesce(:statusCd, sps.status_cd)";
